package delios.psw_exer4_2;

public class Exerc_4_2_2 {

	//Imprimir a soma de todos os valores inteiros entre 1 e 1000, utilizando o comando while.
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1,aux=1;
		System.out.println ("os primeiros 300 inteiros");
		while (i<1001){
			System.out.printf("\t\t %d\n",aux);
			aux+=1;
			i++;
		}
	}

}
